import React, { useState } from "react";
import { FaSistrix } from "react-icons/fa";

const Trends = () => {
  const [keywords, setKeywords] = useState([
    {
      id: 1,
      country: "Trend in Pakistan",
      keyword: "ISPR",
      totalKeywords: "2000k",
    },
    {
      id: 2,
      country: "Trend in Pakistan",
      keyword: "IMRANKAHN",
      totalKeywords: "6000k",
    },
    {
      id: 3,
      country: "Trend in Pakistan",
      keyword: "YoutubeinPakistan",
      totalKeywords: "2560k",
    },
  ]);

  const handleSearch = (event) => {
    // Retrieve search input value from event.target or using state management library (e.g., useState)
    const searchValue = event.target.value;

    // Perform filtering or searching logic based on the search value
    // Here, I'm filtering the keywords based on keyword text containing the search value
    const filteredKeywords = keywords.filter((keyword) =>
      keyword.keyword.toLowerCase().includes(searchValue.toLowerCase())
    );

    // Update the filtered keywords
    setKeywords(filteredKeywords);
  };

  return (
    <div className="trends">
      <div className="trends__search">
        <input
          type="text"
          className="trend__control"
          placeholder="Search Twitter"
          onChange={handleSearch}
        />
        <div className="trend__icon">
          <FaSistrix className="search" />
        </div>
      </div>
      <div className="keywords">
        <div className="key">
          <div className="keyword__heading">
            <h4>Trends for you</h4>
          </div>
          {keywords.map((keyword) => (
            <div key={keyword.id}>
              <div className="country">{keyword.country}</div>
              <div className="keyword__name">
                <strong>{keyword.keyword}</strong>
              </div>
              <div className="keyword__tweets">{keyword.totalKeywords}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Trends;
