import React, { useState } from "react";
import a from '../assets/a.jpg'
import {
  FaRegImage,
  FaRegListAlt,
  FaRegSmile,
  FaCalendarCheck,
  FaComment,
  FaHeart,
  FaRegCheckCircle
} from "react-icons/fa";

const CreateTweet = ({ createNewTweet }) => {
  const [newTweet, setNewTweet] = useState("");

  const handleInputChange = (e) => {
    setNewTweet(e.target.value);
  };

  const handleCreateTweet = () => {
    if (newTweet.trim() !== "") {
      createNewTweet(newTweet);
      setNewTweet("");
    }
  };

  return (
    <div className="create">
      <div className="create__first">
        <div className="create__img">
          <img src={a} alt="profile img" />
        </div>
        <div className="create__input">
          <input
            type="text"
            className="create__control"
            placeholder="What's happening?"
            value={newTweet}
            onChange={handleInputChange}
          />
        </div>
      </div>
      <div className="create__second">
        <div className="create__icons">
          <FaRegImage className="ic" />
          <FaRegListAlt className="ic" />
          <FaRegSmile className="ic" />
          <FaCalendarCheck className="ic" />
        </div>
        <div className="create__btn">
          <button onClick={handleCreateTweet}>Tweet</button>
        </div>
      </div>
    </div>
  );
};

const Tweet = ({ tweet }) => {
  return (
    <div className="tweet">
      <div className="tweet__first">
        <div className="tweet__img">
          <img src={tweet.image} alt="profile img" />
        </div>
        <div className="tweet__details">
          <div className="tweet__name">
            <strong>{tweet.firstName}</strong>{" "}
            <FaRegCheckCircle className="verify" />
          </div>
          <div className="tweet__username">@{tweet.username}</div>
          <div className="tweet__date">{tweet.date}</div>
        </div>
      </div>
      <div className="tweet__message">{tweet.tweet}</div>
      <div className="tweet__reactions">
        <div className="tweet__comments">
          <FaComment /> {tweet.numberOfComments}
        </div>
        <div className="tweet__likes">
          <FaHeart /> {tweet.numberOfLikes}
        </div>
      </div>
    </div>
  );
};

const Tweets = () => {
  const [tweets, setTweets] = useState([]);

  const createNewTweet = (newTweet) => {
    const newTweetObject = {
      id: tweets.length,
      username: "your_username",
      date: new Date().toISOString().split("T")[0],
      tweet: newTweet,
      numberOfLikes: 0,
      numberOfComments: 0,
      image: null,
      firstName: "Your Name"
    };

    setTweets([...tweets, newTweetObject]);
  };

  return (
    <div>
      <CreateTweet createNewTweet={createNewTweet} />
      {tweets.map((tweet) => (
        <Tweet key={tweet.id} tweet={tweet} />
      ))}
    </div>
  );
};

export default Tweets;
