import React, { useState } from "react";
import a from '../assets/a.jpg'
import {
  FaRegImage,
  FaRegListAlt,
  FaRegSmile,
  FaCalendarCheck,
} from "react-icons/fa";

const CreateTweet = ({ createTweet }) => {
  const [newTweet, setNewTweet] = useState("");

  const handleInputChange = (e) => {
    setNewTweet(e.target.value);
  };

  const handleCreateTweet = () => {
    if (newTweet.trim() !== "") {
      createTweet(newTweet);
      setNewTweet("");
    }
  };

  return (
    <div className="create">
      <div className="create__first">
        <div className="create__img">
          <img src={a} alt="profile img" />
        </div>
        <div className="create__input">
          <input
            type="text"
            className="create__control"
            placeholder="What's happening?"
            value={newTweet}
            onChange={handleInputChange}
          />
        </div>
      </div>
      <div className="create__second">
        <div className="create__icons">
          <FaRegImage className="ic" />
          <FaRegListAlt className="ic" />
          <FaRegSmile className="ic" />
          <FaCalendarCheck className="ic" />
        </div>
        <div className="create__btn">
          <button onClick={handleCreateTweet}>Tweet</button>
        </div>
      </div>
    </div>
  );
};

export default CreateTweet;
