import React, { useState } from "react";
import {
  FaRegCheckCircle,
  FaComment,
  FaRegChartBar,
  FaHeart,
  FaLeaf,
  FaRetweet,
  FaShare
} from "react-icons/fa";
import a from '../assets/a.jpg'
import b from '../assets/b.jpg'
import c from '../assets/c.jpg'

const Posts = () => {
  const [tweets, setTweets] = useState([
    {
        id : 0,
              username: "tech_geek",
              date: "2023-02-08",
              tweet: "Just got my hands on the latest smartphone and I am blown away by its features and performance! #techlove",
              numberOfLikes: 250,
              numberOfComments: 60,
              image: b,
              firstName: "John"
            },
            {
        id : 1,
              username: "foodie_gal",
             date: "2023-02-08",
              tweet: "Had the most amazing meal at the new restaurant in town. The flavors were out of this world! #foodie #yum",
              numberOfLikes: 170,
              numberOfComments: 40,
              firstName: "Jane",
              image: b,
            },
            {
        id : 2,
              username: "bookworm",
             date: "2023-02-09",
              tweet: "Just finished reading the latest bestseller and I can't wait to discuss it with my book club next week! #booknerd #booklover",
              numberOfLikes: 200,
              numberOfComments: 50,
              firstName: "Jack",
              image: c,
  
            },
            {
        id :  3,
              username: "fitness_fanatic",
             date: "2023-02-09",
              tweet: "Had an intense workout at the gym this morning and feeling great! #fitnessmotivation #fitlife",
              numberOfLikes: 140,
              numberOfComments: 30,
              firstName: "Jessica",
              image: b
            },
            {
        id : 4,
              username: "nature_lover",
             date: "2023-02-10",
              tweet: "Just took a hike in the mountains and it was breathtaking! The beauty of nature never ceases to amaze me. #outdooradventures #hiking",
              numberOfLikes: 180,
              numberOfComments: 40,
              image: b,
              firstName: "Sarah"
            },
        
            {
        id : 5,
              username: "coffee_lover",
             date: "2023-02-10",
              tweet: "My twitter anniversary today!",
              numberOfLikes: 180,
              numberOfComments: 40,
              image: a,
              firstName: "Gill"
            },
        
            {
        id : 6,
              username: "cool_dude",
             date: "2023-02-10",
              tweet: "Monday blues ;/",
              numberOfLikes: 180,
              numberOfComments: 40,
              image: b,
              firstName: "Stella"
            },
        
            {
        id : 7,
              username: "the_perfectionist",
             date: "2023-02-10",
              tweet: "Good things come to those who wait",
              numberOfLikes: 18,
              image: b,
              numberOfComments: 40,
              firstName: "Adam"
            },
        
            {
        id : 8,
              username: "unknown000",
             date: "2023-02-10",
              tweet: "Today is my Birthday :)",
              numberOfLikes: 10,
              numberOfComments: 4,
              image: b,
              firstName: "Justin"
            },
            {
        id : 9,
              username: "jamesbond",
             date: "2023-02-10",
              tweet: "You need to fight for you want. #hustle #life",
              numberOfLikes: 120,
              numberOfComments: 47,
              image: b,
              firstName: "Elon"
            }
    // Existing tweets data
  ]);
  const saveTweet = (tweet) => {
    setTweets((prevTweets) => [...prevTweets, tweet]);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    
    // Retrieve input values from event.target or using state management library (e.g., useState)
    const username = event.target.elements.username.value;
    const date = event.target.elements.date.value;
    const tweetContent = event.target.elements.tweet.value;
    const numberOfLikes = parseInt(event.target.elements.numberOfLikes.value);
    const numberOfComments = parseInt(event.target.elements.numberOfComments.value);
    const firstName = event.target.elements.firstName.value;
    const image = event.target.elements.image.value;
    const newTweet = {
        id: tweets.length,
        username,
        date,
        tweet: tweetContent,
        numberOfLikes,
        numberOfComments,
        firstName,
        image,
      };
  
      // Call saveTweet function with the new tweet object
      saveTweet(newTweet);
  
      // Reset form inputs if needed
      event.target.reset();
    };

  return (
    <div>
      {tweets.map((tweet) => (
        <div className="postss" key={tweet.id}>
          <div className="postss__first">
            <div className="posts__first__img">
              <img src={tweet.image} alt="Profile" className="profile-image" />
            </div>
            <div className="posts__first__name">
              <strong>{tweet.firstName}</strong>{" "}
              <FaRegCheckCircle className="verify" />
            </div>
            <div className="posts__first__username">
              @{tweet.username} <span>6m</span>
            </div>
          </div>
          <div className="postss__details">
            <div className="postss__details__msg">{tweet.tweet}</div>
            <div className="reactions">
              <span>
                <FaComment className="re" /> {tweet.numberOfComments}
              </span>
              <span>
                <FaHeart className="re" /> {tweet.numberOfLikes}
              </span>
              <span>
                <FaRetweet/>
              </span>
              <span>
                <FaShare/>
              </span>
            </div>
          </div>
        </div>
      ))}

      {/* New tweet form */}
      <form onSubmit={handleFormSubmit}>
        <input type="text" placeholder="Username" name="username" />
        <input type="text" placeholder="Tweet content" name="tweet" />
        <input type="text" placeholder="Number of Likes" name="numberOfLikes" />
        <input type="text" placeholder="Number of Comments" name="numberOfComments" />
        <input type="text" placeholder="First Name" name="firstName" />
        <input type="text" placeholder="Image URL" name="image" />
        <button type="submit">Save Tweet</button>
      </form>
    </div>
  );
};

export default Posts;
